mapper.update:

update users
set usertype = #{userType}
where userid = #{userId}

mapper.select:

  		select *
		from (
    		select rownum rn,
       			   a.*
    		from (
        		   select *
        		   from workboard
        		   where creatorName = #{creatorName}
        		   order by bno desc
    		) a
		)

		where rn > (#{pageNum} -1) * #{amount} and rn <= #{pageNum} * #{amount}

mapper.update:

update CREATORPAGE
set instaPath = #{instaPath},
	aboutme = #{aboutMe}
where pageid = #{pageId}
