mapper.select:
select * from (select @rownum := @rownum + 1 rn, a.* from (select * from JOBBOARD where
TITLE like '%'||#{searchName}||'%' COMPANYNAME like '%'||#{searchName}||'%' 1 = 1
order by bno desc) a ) b, (select @rownum := 0) c where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

select b.*, a.TITLE, a.USERID, a.COMPANYNAME
from JOBBOARD a inner join JOBBOARDDETAIL b on a.BNO = b.BNO
where b.BNO = #{bno}

mapper.delete:
   	{call
		declare
		begin
   			delete from JOBBOARD where BNO = #{bno};
   			delete from JOBBOARDDETAIL where BNO = #{bno}; 
		end
	}
