mapper.select:
select * from (select rownum rn, a.* from (select * from JOBBOARD where
title like '%'||#{searchName}||'%' companyName like '%'||#{searchName}||'%' content like '%'||#{searchName}||'%' writer like '%'||#{searchName}||'%' content like '%'||#{searchName}||'%' or title like '%'||#{searchName}||'%' 1 = 1
order by bno desc) a ) where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

select b.*, a.title, a.userId, a. companyName
from JOBBOARD a inner join JOBBOARDDETAIL b on a.bno = b.bno
where b.bno = #{bno}

mapper.delete:
   	{call
		declare
		begin
   			delete from JOBBOARD where bno = #{bno};
   			delete from JOBBOARDDETAIL where bno = #{bno}; 
		end
	}
