mapper.select:

select *
from (
              select  @rownum:=@rownum:+1 as rn,
                 a.*
             from (
        select bno,
              rno,
                  reply,
               replyId,
                 replyDate,
                replyUpdate           
      from WORKREPLY where bno = #{bno} order by BNO desc) a ) b, (select          @rownum := 0) c
          ) where rn > (#{cri.pageNum} - 1) * #{cri.amount} and rn <= #{cri.pageNum} * #{cri.amount}
