mapper.select:

		select *
		from(select rownum as rn,
            		a.*
     		 from(select *
          		  from JOBBOARD 
          		  where userId = #{userId}
          		  order by bno desc) a )
		where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}


		select *
		from(select rownum as rn,
            		a.*
     		 from(select *
          		  from insertQuestionPage 
          		  where INSERTID = #{userId}
          		  order by INSERTBNO desc) a )
		where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}
